package uet.barrydevp.towerdefense.Bullet;import javafx.geometry.Point2D;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Game.GameSpriteManager;import uet.barrydevp.towerdefense.Monster.BaseMonster;import uet.barrydevp.towerdefense.Sprite.BaseSprite;import uet.barrydevp.towerdefense.Sprite.BulletSprite;public class BrowBullet extends BaseBullet {    @Override    public boolean init() {        setBulletName(BulletName.Rocket);        return true;    }    public static BrowBullet createBullet(BaseMonster targetMonster, Point2D startPoint) {        BrowBullet bullet = new BrowBullet();        if(bullet.init()) {            bullet.setBaseSprite(new BulletSprite(                    GameSpriteManager.getInstance().browBulletImage,                    null,                    startPoint, 0)            );            bullet.setTargetMonster(targetMonster);            bullet.setMoveSpeed(250);            bullet.setAcceleration(20);            bullet.setMaxForce(70);            bullet.setScope(4);            bullet.addToLayer();        }        return bullet;    }    @Override    public void fire() {        BaseMonster targetMonster = getTargetMonster();        double distance = getBaseSprite().getCenterLocation().distance(targetMonster.getBaseSprite().getCenterLocation());        if(distance < getScope()) {            int monsterHp = targetMonster.getCurHp() - getMaxForce();            if(monsterHp <= 0){                targetMonster.setCurHp(0);                targetMonster.death();            } else {                targetMonster.setCurHp(monsterHp);                targetMonster.getHurt();            }            targetMonster.setHpPercentage(monsterHp * 1.0 / targetMonster.getMaxHp());            removeBullet();        }    }}