package uet.barrydevp.towerdefense.Bullet;import javafx.geometry.Point2D;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Game.GameSpriteManager;import uet.barrydevp.towerdefense.Monster.BaseMonster;import uet.barrydevp.towerdefense.Sprite;public class YellowBullet extends BaseBullet {    @Override    public boolean init() {        setBulletName(BulletName.Rocket);        return true;    }    public static YellowBullet createBullet(BaseMonster targetMonster, Point2D startPoint) {        YellowBullet bullet = new YellowBullet();        if(bullet.init()) {            bullet.setBaseSprite(new Sprite(                    GameManager.getInstance().getBulletLayer(),                    GameSpriteManager.getInstance().yellowBulletImage,                    startPoint, 0)            );            bullet.setTargetMonster(targetMonster);            bullet.setMoveSpeed(250);            bullet.setAcceleration(20);            bullet.setMaxForce(50);            bullet.setScope(4);        }        return bullet;    }    @Override    public void fire() {        BaseMonster targetMonster = getTargetMonster();        double distance = getBaseSprite().getCenterLocation().distance(targetMonster.getBaseSprite().getCenterLocation());        if(distance < getScope()) {            int monsterHp = targetMonster.getCurHp() - getMaxForce();            if(monsterHp <= 0){                targetMonster.setCurHp(0);                targetMonster.death();            } else {                targetMonster.setCurHp(monsterHp);                targetMonster.getHurt();            }            targetMonster.setHpPercentage(monsterHp * 1.0 / targetMonster.getMaxHp());            removeBullet();        }    }}