package uet.barrydevp.towerdefense.Entity;import javafx.scene.layout.Pane;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Sprite.BaseSprite;public abstract class BaseEntity {    protected BaseSprite baseSprite;    protected Pane layer;    public BaseEntity() {        baseSprite = null;        layer = null;    }    public Pane getLayer() {        return layer;    }    public void setLayer(Pane layer) {        this.layer = layer;    }    public BaseSprite getBaseSprite() {        return baseSprite;    }    public void setBaseSprite(BaseSprite baseSprite) {        this.baseSprite = baseSprite;    }    public void addToLayer() {        if(layer != null && baseSprite != null)            layer.getChildren().add(baseSprite);    }    public void removeFromLayer() {        if(layer != null && baseSprite != null){            layer.getChildren().remove(baseSprite);            if(baseSprite.getChildrenSprite() != null) {                GameManager.getInstance().getEffectLayer().getChildren().removeAll(baseSprite.getChildrenSprite().values());            }        }    }}