package uet.barrydevp.towerdefense.Entity.Tower;import javafx.geometry.Point2D;import uet.barrydevp.towerdefense.Entity.Bullet.BlueBullet;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Game.GameSpriteManager;import uet.barrydevp.towerdefense.Entity.Monster.BaseMonster;import uet.barrydevp.towerdefense.Sprite.BaseSprite;import uet.barrydevp.towerdefense.Sprite.EffectSprite;import uet.barrydevp.towerdefense.Sprite.TowerSprite;import java.util.HashMap;public class MiniGunBlue extends BaseTower {    @Override    public boolean init() {        setTowerName(TowerName.MiniGunBlue);        return true;    }    public static MiniGunBlue creatTower(Point2D location) {        MiniGunBlue tower = new MiniGunBlue();        if(tower.init()) {            HashMap<String, BaseSprite> children = new HashMap<>();            children.put(FIRE_EFFECT ,new EffectSprite(GameSpriteManager.getInstance().fireEffect3Image, location, 90));            tower.setBuildMoney(100);            tower.setUpdateMoney(180);            tower.setBaseSprite(                    new TowerSprite(                            TowerName.MiniGunBlue,                            children,                            location, 0,                            tower)            );            tower.setForce(10);            tower.setLevel(1);            tower.setScope(160);            tower.setNextScope(180);            tower.setRate(1);            tower.addToLayer();        }        return tower;    }    @Override    public void upgradeTower() {        BaseTower tower = MiniGunWhite.creatTower(baseSprite.getCenterLocation());        buildTower(tower);    }    public void createBullet(BaseMonster targetMonster, Point2D center) {        BlueBullet bullet = BlueBullet.createBullet(targetMonster, center);        GameManager.getInstance().getBulletArray().add(bullet);    }}