package uet.barrydevp.towerdefense.Entity.Tower;import javafx.geometry.Point2D;import uet.barrydevp.towerdefense.Entity.Bullet.RedBullet;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Game.GameSpriteManager;import uet.barrydevp.towerdefense.Entity.Monster.BaseMonster;import uet.barrydevp.towerdefense.Sprite.BaseSprite;import uet.barrydevp.towerdefense.Sprite.EffectSprite;import uet.barrydevp.towerdefense.Sprite.TowerSprite;import java.util.HashMap;public class MiniGunRed extends BaseTower {    @Override    public boolean init() {        setTowerName(TowerName.MiniGunRed);        return true;    }    public static MiniGunRed creatTower(Point2D location) {        MiniGunRed tower = new MiniGunRed();        if(tower.init()) {            HashMap<String, BaseSprite> children = new HashMap<>();            children.put(FIRE_EFFECT ,new EffectSprite(GameSpriteManager.getInstance().fireEffect3Image, location, 90));            tower.setBuildMoney(180);            tower.setUpdateMoney(240);            tower.setBaseSprite(                    new TowerSprite(                            TowerName.MiniGunRed,                            children,                            location, 0,                            tower)            );            tower.setForce(30);            tower.setLevel(2);            tower.setScope(180);            tower.setNextScope(200);            tower.setRate(1);            tower.addToLayer();        }        return tower;    }    @Override    public void upgradeTower() {        BaseTower tower = MiniGunUltraRed.creatTower(baseSprite.getCenterLocation());        buildTower(tower);    }    public void createBullet(BaseMonster targetMonster, Point2D center) {        RedBullet bullet = RedBullet.createBullet(targetMonster, center);        GameManager.getInstance().getBulletArray().add(bullet);    }}