package uet.barrydevp.towerdefense.Game;import javafx.scene.layout.Pane;import uet.barrydevp.towerdefense.Monster.BaseMonster;import java.util.ArrayList;public class GameManager {    private static GameManager instance;    private Pane gameFieldPane;    private ArrayList<BaseMonster> monsterArray;    private int money;    private int life;    private int level;    private GameManager(){}    public void CreateGameManager() {        monsterArray = new ArrayList<BaseMonster>();        money = 0;        life = 0;        level = 0;        gameFieldPane = new Pane();    }    public static GameManager getInstance(){        if(instance == null){            instance = new GameManager();            instance.CreateGameManager();        }        return instance;    }    public ArrayList<BaseMonster> getMonsterArray() {        return monsterArray;    }    public Pane getGameFieldPane() {        return gameFieldPane;    }    public void setGameFieldPane(Pane gameFieldPane) {        this.gameFieldPane = gameFieldPane;    }    public int getLife() {        return life;    }    public void setLife(int life) {        this.life = life;    }    public int getMoney() {        return money;    }    public void setMoney(int money) {        this.money = money;    }    public int getLevel() {        return level;    }    public void setLevel(int level) {        this.level = level;    }    public void removeMonster(BaseMonster monster) {        monsterArray.remove(monster);    }    public void eraseAll() {        monsterArray.clear();    }}