package uet.barrydevp.towerdefense.Game;import javafx.scene.Group;import javafx.scene.Scene;import javafx.scene.transform.Scale;import uet.barrydevp.towerdefense.Settings;public class GameScene {    public static GameScene instance;    private Scale scale;    private Scene sceneInGame;    private Scene sceneMainMenuGame;    private Scene sceneMenuLevelGame;    private Scene sceneGameOver;    private Scene sceneGameVictory;    private Group groupPaneInGame;    private Group groupPaneMainMenu;    private Group groupPaneMenuLevel;    private Group groupPaneInfoGame;    private Group groupPaneGameOver;    private Group groupGameVictory;    private GameScene() {    }    public static GameScene getInstance() {        if (instance == null) {            instance = new GameScene();            instance.CreateGameScene();        }        return instance;    }    public void CreateGameScene() {        scale = new Scale(Settings.SCALE_WIDTH, Settings.SCALE_HEIGHT);        createSceenInGame();        createSceneMainMenuGame();        createSceneMenuLevelGame();        createSceneGameOver();    }    public void createSceenInGame() {        GameManager instanceGameManager = GameManager.getInstance();        groupPaneInGame = new Group();        groupPaneInGame.getChildren().addAll(                instanceGameManager.getBackgroundLayer(),                instanceGameManager.getMonsterLayer(),                instanceGameManager.getEffectLayer(),                instanceGameManager.getBulletLayer(),                instanceGameManager.getTowerLayer()        );        groupPaneInfoGame = new Group();        groupPaneInfoGame.setTranslateX(Settings.SCENE_WIDTH * 0.8);        groupPaneInfoGame.setTranslateY(0.0);        groupPaneInfoGame.getChildren().add(GameMenu.getInstance().getMenuInfoPane());        Group root = new Group();        root.getChildren().addAll(groupPaneInGame, groupPaneInfoGame);        sceneInGame = new Scene(root, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);        sceneInGame.getRoot().getTransforms().setAll(scale);    }    public Scene getSceneInGame() {        return sceneInGame;    }    public void createSceneMainMenuGame() {        GameMenu instanceGameMenu = GameMenu.getInstance();        groupPaneMainMenu = new Group();        groupPaneMainMenu.getChildren().addAll(                instanceGameMenu.getMainMenuPane()        );        sceneMainMenuGame = new Scene(groupPaneMainMenu, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);        sceneMainMenuGame.getRoot().getTransforms().setAll(scale);    }    public Scene getSceneMainMenuGame() {        return sceneMainMenuGame;    }    public void createSceneMenuLevelGame() {        GameMenu instanceGameMenu = GameMenu.getInstance();        groupPaneMenuLevel = new Group();        groupPaneMenuLevel.getChildren().addAll(                instanceGameMenu.getMenuLevelPane()        );        sceneMenuLevelGame = new Scene(groupPaneMenuLevel, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);        sceneMenuLevelGame.getRoot().getTransforms().setAll(scale);    }    public Scene getSceneMenuLevelGame() {        return sceneMenuLevelGame;    }    public void createSceneGameOver() {        GameMenu instanceGameMenu = GameMenu.getInstance();        groupPaneGameOver = new Group();        groupPaneGameOver.getChildren().addAll(                instanceGameMenu.getGameOverPane()        );        sceneGameOver = new Scene(groupPaneGameOver, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);        sceneGameOver.getRoot().getTransforms().setAll(scale);    }    public Scene getSceneGameOver() {        return sceneGameOver;    }    public void createSceneGameVictory() {        GameMenu instanceGameMenu = GameMenu.getInstance();        groupGameVictory = new Group();        groupGameVictory.getChildren().addAll(                instanceGameMenu.getGameVictoryGame()        );        sceneGameVictory = new Scene(groupGameVictory, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);        sceneGameVictory.getRoot().getTransforms().setAll(scale);    }    public Scene getSceneGameVictory() {        return sceneGameVictory;    }}