package uet.barrydevp.towerdefense.Game;import javafx.scene.Group;import javafx.scene.Scene;import javafx.scene.layout.Pane;import uet.barrydevp.towerdefense.Settings;public class GameScene {    public static GameScene instance;    private Scene sceneInGame;    private Scene sceneMenu;    private Scene sceneGameOver;    private Group groupPaneInGame;    private Group groupPaneMenu;    private Group groupPaneGameOver;    private GameScene(){}    public void CreateGameScene() {        createSceenInGame();    }    public static GameScene getInstance(){        if(instance == null){            instance = new GameScene();            instance.CreateGameScene();        }        return instance;    }    private void createSceenInGame() {        GameManager instanceGameManager = GameManager.getInstance();        groupPaneInGame = new Group();        groupPaneInGame.getChildren().addAll(                instanceGameManager.getBackgroundLayer(),                instanceGameManager.getMonsterLayer(),                instanceGameManager.getEffectLayer(),                instanceGameManager.getBulletLayer(),                instanceGameManager.getTowerLayer()        );        sceneInGame = new Scene( groupPaneInGame, Settings.SCENE_WIDTH, Settings.SCENE_HEIGHT);    }    public Scene getSceneInGame() {        return sceneInGame;    }}