package uet.barrydevp.towerdefense.Game;import javafx.scene.image.Image;public class GameSpriteManager {    private static GameSpriteManager instance;    public Image greenMinionImage;    public Image redMinionImage;    public Image silverMinionImage;    public Image browMinionImage;    public Image greenPlaneImage;    public Image silverPlaneImage;    private GameSpriteManager(){}    public void createGameSpriteManager() {        greenMinionImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile245.png").toExternalForm());        redMinionImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile246.png").toExternalForm());        silverMinionImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile248.png").toExternalForm());        browMinionImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile247.png").toExternalForm());        greenPlaneImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile270.png").toExternalForm());        silverPlaneImage = new Image( getClass().getResource("/PNG/Defaultsize/towerDefense_tile271.png").toExternalForm());    }    public static GameSpriteManager getInstance(){        if(instance == null){            instance = new GameSpriteManager();            instance.createGameSpriteManager();        }        return instance;    }}