package uet.barrydevp.towerdefense.Map;import javafx.geometry.Point2D;import uet.barrydevp.towerdefense.Game.GameManager;import uet.barrydevp.towerdefense.Monster.BaseMonster;import uet.barrydevp.towerdefense.Monster.Bosses.GreenPlane;import uet.barrydevp.towerdefense.Monster.Bosses.SilverPlane;import uet.barrydevp.towerdefense.Monster.Minions.BrowMinion;import uet.barrydevp.towerdefense.Monster.Minions.GreenMinion;import uet.barrydevp.towerdefense.Monster.Minions.RedMinion;import uet.barrydevp.towerdefense.Monster.Minions.SilverMinion;import uet.barrydevp.towerdefense.Monster.MonsterName;import uet.barrydevp.towerdefense.Sprite;import java.util.ArrayList;public abstract class BaseMap {    private int level;    private Sprite mapSprite;    private int difficulty;    private boolean isEnd;    private boolean isStart;    private int wave;    private int maxWave;    private int turn;    private double elapsedTimeWave;    private double elapsedTimeTurn;    public ArrayList<Point2D> pointsArray;    public ArrayList<ArrayList<MonsterName>> waveArray;    public BaseMap() {        isEnd = false;        isStart = false;        wave = 0;        maxWave = 0;        turn = 0;        elapsedTimeWave = 0;        elapsedTimeTurn = 0;        pointsArray = new ArrayList<>();        waveArray = new ArrayList<>();    }    public int getLevel() {        return level;    }    public void setLevel(int level) {        this.level = level;    }    public int getWave() {        return wave;    }    public void setWave(int wave) {        this.wave = wave;    }    public int getDifficulty() {        return difficulty;    }    public void setDifficulty(int difficulty) {        this.difficulty = difficulty;    }    public double getElapsedTimeTurn() {        return elapsedTimeTurn;    }    public void setElapsedTimeTurn(double elapsedTimeTurn) {        this.elapsedTimeTurn = elapsedTimeTurn;    }    public double getElapsedTimeWave() {        return elapsedTimeWave;    }    public void setElapsedTimeWave(double elapsedTimeWave) {        this.elapsedTimeWave = elapsedTimeWave;    }    public int getMaxWave() {        return maxWave;    }    public void setMaxWave(int maxWave) {        this.maxWave = maxWave;    }    public int getTurn() {        return turn;    }    public void setTurn(int turn) {        this.turn = turn;    }    public Sprite getMapSprite() {        return mapSprite;    }    public void setMapSprite(Sprite mapSprite) {        this.mapSprite = mapSprite;    }    public boolean isEnd() {        return isEnd;    }    public void setEnd(boolean end) {        isEnd = end;    }    public boolean isStart() {        return isStart;    }    public void setStart(boolean start) {        isStart = start;    }    public abstract void addPointsArray();    public abstract void addWave();    public void spawnMonster(double elapsedTime) {        if(isStart && !isEnd) {            if(waveArray != null && waveArray.size() > 0) {                if(turn == -1) {                    if(elapsedTimeWave > 3.0) {                        turn = 0;                        elapsedTimeWave = 0;                    } else {                        elapsedTimeWave += elapsedTime;                    }                }                if(turn > -1) {                    if(elapsedTimeTurn > 1.0) {                        BaseMonster monster = null;                        System.out.println("wave:" + wave);                        System.out.println("turn: " + turn);                        switch (waveArray.get(wave).get(turn)) {                            case GreenMinion:                                monster = GreenMinion.createMonster(pointsArray);                                break;                            case SilverMinion:                                monster = SilverMinion.createMonster(pointsArray);                                break;                            case RedMinion:                                monster = RedMinion.createMonster(pointsArray);                                break;                            case BrowMinion:                                monster = BrowMinion.createMonster(pointsArray);                                break;                            case GreenPlane:                                monster = GreenPlane.createMonster(pointsArray);                                break;                            case SilverPlane:                                monster = SilverPlane.createMonster(pointsArray);                                break;                        }                        if(monster != null){                            GameManager.getInstance().getMonsterArray().add(monster);                        }                        if(++turn >= waveArray.get(wave).size()) {                            turn = -1;                            if(++wave >= maxWave) {                                --wave;                                isStart = false;                            }                        }                        elapsedTimeTurn = 0;                    } else {                        elapsedTimeTurn += elapsedTime;                    }                }            }        } else {            if(wave == maxWave) {                if(GameManager.getInstance().getMonsterArray().size() == 0){                    isEnd = true;                }            }        }    }}